import React from 'react';

class Testimonial extends React.Component
{
    render(){
        return( 
            <div className="ml-6 min-w-sm md:min-w-md lg:min-w-lg transform-gpu" style={{...this.props.location}}>
            <div className="">
              <div className="bg-white border-gray-200">
                <p className="text-brandGreen text-4xl pb-3"><i class="fas fa-quote-left"></i></p>
                <p className="text-white text-2xl pb-3">{this.props.person.quote}</p>
                <p className="text-brandGreen text-4xl pb-3 text-right"><i class="fas fa-quote-right"></i></p>              
              </div>
              <div className="p-6 mx-auto border border-gray-200 bg-gray-100">

                    <p className="text-white text-center font-display text-semibold text-2xl">{this.props.person.name}</p>
                    <p className="text-gray-200 text-center text-lg">{this.props.person.title}</p>

              </div>
            </div>
          </div>                      
        );
    }
}

export default Testimonial;
