import React from 'react';
import {ReactComponent as QuoteIcon} from '../images/quote.svg';

class Testimonial extends React.Component
{
    render(){
        return( 
            <div className="ml-6 min-w-sm md:min-w-md lg:min-w-lg transform-gpu" style={{...this.props.location}}>
              <div className="bg-gray-900 p-6 relative">
                <QuoteIcon className="bg-brandGreen absolute"></QuoteIcon>
                <p className="text-white text-lg py-6">{this.props.person.quote}</p>  
                <hr className="border-t border-gray-700"></hr>
                <p className="text-white text-center font-display text-semibold text-2xl pt-4">{this.props.person.name}</p>
                <p className="text-gray-200 text-center text-xl">{this.props.person.title}</p>     
              </div>
          </div>                      
        );
    }
}

export default Testimonial;
