import React from 'react';

window.onscroll = displayScrollButton;

function displayScrollButton()
{
    var button = document.getElementById('scroll_button');
    if(document.body.scrollTop > 20 || document.documentElement.scrollTop > 20)
    {
        button.style.display = 'block';
    }
    else 
    {
        button.style.display = 'none';
    }
}

function scrollToTop()
{
    window.scrollTo({top: 0, behavior: 'smooth'});
}

function ScrollButton()
{
    return( 
        <button id="scroll_button" className="fixed bg-gray-800 rounded-full border border-gray-700 bottom-6 right-6 h-16 w-16 z-30 group transition" onClick={scrollToTop}>
            <p className="text-white text-3xl group-hover:text-brandGreen transition"><i class="fas fa-arrow-up"></i></p>
        </button>                    
    );
}

export default ScrollButton;
