import React from 'react';

class Testimonial extends React.Component
{
    render(){
        return( 
            <div className="ml-6 min-w-sm md:min-w-md lg:min-w-lg transform-gpu" style={{...this.props.location}}>
            <div className="shadow-lg border border-gray-200">
              <div className="bg-white p-6">
                <p className="text-brand-green text-2xl pb-3"><i class="fas fa-quote-left"></i></p>
                <p className="text-gray-800 text-lg pb-3">{this.props.person.quote}</p>             
              </div>
              <div className="bg-gray-100 border-gray-200 border-t p-6">
                <div className="mx-auto">
                  <div>
                    <p className="text-black text-center font-display text-semibold text-2xl">{this.props.person.name}</p>
                    <p className="text-gray-500 text-center text-xl">{this.props.person.title}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>                      
        );
    }
}

export default Testimonial;
