import React from 'react';
import {Link} from "react-router-dom";
import ExternalLink from './ExternalLink';
import EnsembleImage from '../images/ensemble-project-image.png';
import TraneLogo from '../images/trane-logo.png';
import EnsembleInitialConcepts from '../images/ensemble-initial-concepts.jpg';
import EnsembleRefiningDesign from '../images/ensemble-refining-design.png';
import EnsembleRolesNew from '../images/ensemble-roles-new.PNG';
import EnsembleRolesOld from '../images/ensemble-roles-old.PNG';

initComparisons();

function Project1()
{
    return( 
        <div className="bg-black py-20">
            <section className="px-4 max-w-4xl mx-auto py-6">
                <div className="flex flex-row mb-3">
                    <div className="bg-black p-2 w-auto border border-gray-900 mr-6">
                        <p className="text-brandGreen font-mono text-center">Project</p>
                        <p className="text-white font-mono text-center text-6xl">01</p>
                    </div>
                    <div className="flex flex-col">
                    <p className="font-display text-white font-semibold text-3xl md:text-4xl lg:text-5xl">Redesigning user permissions for Tracer Ensemble</p>
                    </div>               
                </div>
                <div className="flex flex-row space-x-4">
                    <img src={TraneLogo} className="h-8"></img>
                    <p className="text-gray-400 text-lg pt-1 pb-4">5 min read&nbsp;&nbsp;//&nbsp;&nbsp;July 2020 - January 2021</p>
                </div>
            </section>
            <section className="flex flex-row">
                <div className="flex flex-col w-5/12">
                    <div className="bg-black h-1/5"></div>
                    <div className="bg-white h-4/5"></div>
                </div>
                <img src={EnsembleImage} className="w-full max-w-4xl"></img>
                <div className="flex flex-col w-5/12">
                    <div className="bg-black h-1/5"></div>
                    <div className="bg-white h-4/5"></div>
                </div>
            </section>
            <section className="bg-white py-4">
                <div className="max-w-4xl mx-auto px-4 md:px-0">
                    <div className="grid md:grid-cols-3 md:space-x-4 space-y-2 md:space-y-0 mb-8">
                        <div className="bg-white p-8 shadow-lg border border-gray-200">
                            <p className="text-black font-semibold font-display text-2xl pb-2">My Role</p>
                            <hr></hr>
                            <p className="text-gray-800 text-lg pt-4">Sole UX resource assigned to project. Responsible for all user research & design work</p>
                        </div>
                        <div className="bg-white p-8 shadow-lg border border-gray-200">
                            <p className="text-black font-semibold font-display text-2xl pb-2">Project Team</p>
                            <hr></hr>
                            <p className="text-gray-800 text-lg pt-4">2 Product Owners, Portfolio Manager, Product Planning Engineer, Software Developers</p>
                        </div>
                        <div className="bg-white p-8 shadow-lg border border-gray-200">
                            <p className="text-black font-semibold font-display text-2xl pb-2">Tools I Used</p>
                            <hr></hr>
                            <p className="text-gray-800 text-lg pt-4">Axure, Microsoft Teams, Confluence, PowerPoint, Miro, Cisco WebEx</p>
                        </div>    
                    </div>
                    <p className="text-black text-4xl font-display font-semibold pb-4">Background</p>
                    <p className="text-black text-lg mb-8">Ensemble users care a lot about control. The primary purpose of the product is to give 
                    Building Operators and Facilities Managers a one-stop-shop to control and monitor their buildings. And there's a plethora of
                    features that allow them to do just that.<br></br><br></br> They also care about controlling who has access to these features. Today, Ensemble
                    addresses this need by allowing site administrators to assign roles to other users. These roles determine what permissions they have access to.<br></br><br></br>
                    Customers had shared feedback that the current solution wasn't flexible enough. They wanted to be able to pick and choose 
                    the permissions each role had access to. I was brought in to design a more flexible workflow.
                    </p>
                    <p className="text-black text-4xl font-display font-semibold py-4">Identifying User Needs</p>
                    <p className="text-black text-lg">I started the design process by figuring out what the users' needs were. I was new to the 
                    team, so I met with stakeholders to learn more about who the users are and why they use the product. Together, we identified 
                    three problems that we needed to address with the redesign:                    
                    </p>
                    <div className="grid md:grid-cols-3 md:space-x-4 space-y-2 md:space-y-0 pt-4 pb-8">
                        <div className="bg-white p-8 shadow-lg border border-gray-200">
                            <p className="text-black font-semibold font-display text-2xl pb-2">Problem #1</p>
                            <hr></hr>
                            <p className="text-gray-800 text-lg pt-4">Users can't setup permissions the way they want to.</p>
                        </div>
                        <div className="bg-white p-8 shadow-lg border border-gray-200">
                            <p className="text-black font-semibold font-display text-2xl pb-2">Problem #2</p>
                            <hr></hr>
                            <p className="text-gray-800 text-lg pt-4">Users don't understand our terminology.</p>
                        </div>
                        <div className="bg-white p-8 shadow-lg border border-gray-200">
                            <p className="text-black font-semibold font-display text-2xl pb-2">Problem #3</p>
                            <hr></hr>
                            <p className="text-gray-800 text-lg pt-4">The current permissions structure is hard to maintain.</p>
                        </div>    
                    </div>
                    <p className="text-black text-4xl font-display font-semibold pb-4">Initial Concepts</p>
                    <p className="text-black text-lg pb-4">Users wanted more control over permissions than the original design could provide. 
                    To fulfill this desire, I knew that I would have to redesign the workflow for setting up permissions. The challenge was 
                    finding a way to give users the flexibility that they wanted while also making it easy to for them to understand. <br></br><br></br>
                    I started sketching some early concepts in my notebook (I always keep one within arm's reach) before moving to Axure to flesh out my ideas.                   
                    </p>
                    <img src={EnsembleInitialConcepts} className="mx-auto border border-gray-200"></img>
                    <p className="text-gray-500 mx-auto pt-2 pb-8 max-w-3xl text-center">A senior UX Designer who was familiar with Ensemble provided early feedback 
                    and helped familiarize me with the Ensemble design patterns.</p>
                    <p className="text-black text-4xl font-display font-semibold pb-4">Customer Feedback</p>
                    <p className="text-black text-lg">After putting together a prototype, I sought feedback from customers to determine if the design was suitable. 
                    I recruited individuals from a variety of market segments to ensure that my sample adequately represented our user base. I met with each individual 
                    virtually. I shared my screen while I asked them questions. The questions were designed to figure out users' preferences and to determine if they 
                    understood the design. The biggest takeaway from these sessions was that customers generally didn't know about the user management features we already had.                   
                    </p>
                    <div className="grid md:grid-cols-3 md:space-x-4 space-y-2 md:space-y-0 pt-4 pb-8">
                        <div className="bg-white p-8 shadow-lg border border-gray-200">
                            <p className="text-black font-semibold font-display text-2xl pb-2">Quote #1</p>
                            <hr></hr>
                            <p className="text-gray-800 text-lg pt-4">This is much better than how I do it today.</p>
                        </div>
                        <div className="bg-white p-8 shadow-lg border border-gray-200">
                            <p className="text-black font-semibold font-display text-2xl pb-2">Quote #2</p>
                            <hr></hr>
                            <p className="text-gray-800 text-lg pt-4">I didn't know this feature existed, but I like it.</p>
                        </div>
                        <div className="bg-white p-8 shadow-lg border border-gray-200">
                            <p className="text-black font-semibold font-display text-2xl pb-2">Quote #3</p>
                            <hr></hr>
                            <p className="text-gray-800 text-lg pt-4">The new workflow makes me confident that I'm going to get what I expect when I set up a new user.</p>
                        </div>    
                    </div>
                    <p className="text-black text-4xl font-display font-semibold pb-4">Refining the Design</p>
                    <p className="text-black text-lg pb-4">I used the research findings to improve my design. To help customers discover the feature, I renamed the 
                    permissions setup page and all links to the page from "User Roles" to "Roles" (we learned that users often confused User Roles with Users).<br></br><br></br>
                    We learned that users typically give people the same level of access for all equipment, so I added an accelerator to account for this behavior.                
                    </p>
                    <img src={EnsembleRefiningDesign} className="mx-auto border border-gray-200"></img>
                    <p className="text-gray-500 mx-auto pt-2 pb-8 max-w-3xl text-center">Adding this option saved 11 clicks.</p>
                    <p className="text-black text-4xl font-display font-semibold pb-4">Usability Testing</p>
                    <p className="text-black text-lg pb-8">With the time left before the project deadline, I conducted a usability test. I wanted to 
                    discover if there were any unforeseen usability issues before I handed off the design to the developers. I recruited three participants 
                    and wrote a task list for them to complete. The task list required them to perform user management tasks as they would in the real world.<br></br><br></br>
                    While we didn't uncover any glaring design flaws during usability testing, we did observe that users spent more time trying to find permissions 
                    in the permissions wizard than we originally thought. My solution to this problem was to migrate the permissions wizard from a modal to a full page. 
                    I suspected that it would be easier for users to scan and find permissions if we displayed more permissions at a time.                 
                    </p>
                    <p className="text-black text-4xl font-display font-semibold pb-4">Outcome</p>
                    <p className="text-black text-lg pb-8">I created the final visual design by following our style guidelines and handed it off to 
                    the developers. At the time of me writing this, the design has not yet been implemented. We got a lot of positive feedback from customers 
                    throughout the design process, so I'm confident the feature will be well-received when it is released.              
                    </p>
                    <p className="text-black text-4xl font-display font-semibold pb-4">Before and After</p>
                    <div className="flex flex-row">
                        <img className="" src={EnsembleRolesOld}></img>
                        <img className="" src={EnsembleRolesNew}></img>
                    </div>
                </div>
            </section>
        </div>                 
    );
}

function initComparisons() {
    var x, i;
    /* Find all elements with an "overlay" class: */
    x = document.getElementsByName("overlay");
    for (i = 0; i < x.length; i++) {
      /* Once for each "overlay" element:
      pass the "overlay" element as a parameter when executing the compareImages function: */
      compareImages(x[i]);
    }
    function compareImages(img) {
      var slider, img, clicked = 0, w, h;
      /* Get the width and height of the img element */
      w = img.offsetWidth;
      h = img.offsetHeight;
      /* Set the width of the img element to 50%: */
      img.style.width = (w / 2) + "px";
      /* Create slider: */
      slider = document.createElement("DIV");
      slider.setAttribute("class", "img-comp-slider");
      /* Insert slider */
      img.parentElement.insertBefore(slider, img);
      /* Position the slider in the middle: */
      slider.style.top = (h / 2) - (slider.offsetHeight / 2) + "px";
      slider.style.left = (w / 2) - (slider.offsetWidth / 2) + "px";
      /* Execute a function when the mouse button is pressed: */
      slider.addEventListener("mousedown", slideReady);
      /* And another function when the mouse button is released: */
      window.addEventListener("mouseup", slideFinish);
      /* Or touched (for touch screens: */
      slider.addEventListener("touchstart", slideReady);
       /* And released (for touch screens: */
      window.addEventListener("touchend", slideFinish);
      function slideReady(e) {
        /* Prevent any other actions that may occur when moving over the image: */
        e.preventDefault();
        /* The slider is now clicked and ready to move: */
        clicked = 1;
        /* Execute a function when the slider is moved: */
        window.addEventListener("mousemove", slideMove);
        window.addEventListener("touchmove", slideMove);
      }
      function slideFinish() {
        /* The slider is no longer clicked: */
        clicked = 0;
      }
      function slideMove(e) {
        var pos;
        /* If the slider is no longer clicked, exit this function: */
        if (clicked == 0) return false;
        /* Get the cursor's x position: */
        pos = getCursorPos(e)
        /* Prevent the slider from being positioned outside the image: */
        if (pos < 0) pos = 0;
        if (pos > w) pos = w;
        /* Execute a function that will resize the overlay image according to the cursor: */
        slide(pos);
      }
      function getCursorPos(e) {
        var a, x = 0;
        e = e || window.event;
        /* Get the x positions of the image: */
        a = img.getBoundingClientRect();
        /* Calculate the cursor's x coordinate, relative to the image: */
        x = e.pageX - a.left;
        /* Consider any page scrolling: */
        x = x - window.pageXOffset;
        return x;
      }
      function slide(x) {
        /* Resize the image: */
        img.style.width = x + "px";
        /* Position the slider: */
        slider.style.left = img.offsetWidth - (slider.offsetWidth / 2) + "px";
      }
    }
  }

export default Project1;
